mapper.update:
UPDATE board_posts
SET title = #{title}, content = #{content}, author = #{author}, subject = #{subject}, category_id = #{categoryId}
WHERE id = #{id}

mapper.select:
select * from board_posts
order by id DESC
limit #{limit} offset #{offset};

SELECT *
FROM board_posts
WHERE category_id = #{categoryId}  
ORDER BY id DESC
LIMIT #{limit} OFFSET #{offset};
